find /media/SED/audible -type d  | grep -v "\/[0-9]*$" | grep -v Subsection | grep "/media/SED/audible/.*/.*" | grep -v Salvatore | grep -v Part.[0-9]* | grep -v Story.[0-9]* | grep -v Chapter.[0-9]* | grep -v Stave.[0-9]* | grep -v Section.[0-9]* | grep -v Day.[0-9]*$ | grep -v Book.[0-9]*$ | grep -v Watch/[1-7] | grep -v StephenFry/ | grep -v "Complete Stories of Sherlock Holmes/" | grep -v Rainbow/ | grep -v Reamde/ | grep -v "Fellowship of the Ring/" | grep -v "The Two Towers/" | grep -v "The Return of the King/" | grep -v "Silmarillion/" | grep -v "archive.org/" | grep -v "Middle Earth/" | grep -v "By Episode/" | grep -v "Chronological Order/" | sort > ~/alldirs.txt
sed -i 's/\/media\/SED\/audible\///' alldirs.txt
